import { FaMagnifyingGlass } from "react-icons/fa6";
import { Link, Outlet } from "react-router-dom";
import { FaBars } from "react-icons/fa6";
import { useState } from "react";
import Burger from "./Burger";
import "../styles/Navbar.scss";
import "../styles/Burger.scss";





function Navbar() {
	const [toggle, setToggle] = useState(false)
	const toggleMenu = () => {
		setToggle(!toggle)
	}

	
	return (
			<>
		<div className='navbar'>
			<div className={toggle ? "show-menu" : "left-side-navbar"}>
				<FaBars className="fabar"  onClick={toggleMenu}/>

				<Link className='navbar-link' to={"/movies"}>
					Movies
				</Link>
				<Link className='navbar-link' to={"/series"}>
					Series
				</Link>
			</div>
			<Link className='navbar-link main' to={"/"}>
				MoviesSearch
			</Link>
			<div className='right-side-navbar'>
				<Link className='navbar-link' to={"/search"}>
					<FaMagnifyingGlass style={{color: "white", height: "1.5rem"}} />
				</Link>
				<Link className='navbar-link' to={"/profile"}>
					Profile
				</Link>
			</div>
		</div>
		<Outlet />
			</>
	);
}

export default Navbar;
